mapper.update:
update dish
dish.name = #{name}, dish.category_id = #{categoryId}, dish.price = #{price}, dish.image = #{image}, dish.description = #{description}, dish.status = #{status}, dish.update_time = #{updateTime}, dish.update_user = #{updateUser},
where id = #{id}

mapper.select:
select  dish.*,category.name as category_name from dish left outer join category on dish.category_id = category.id


    and dish.name like concat('%',#{name},'%')  
    and dish.category_id = #{categoryId} 
     and dish.status = #{status}

order by create_time desc
